var measure = function () {

    function GetRandomNumber(from, to) {
        return Math.floor((Math.random() * (to - from + 1)) + from);
    }

    function buildMeteoMessage() {

        //temperature (in celcius)
        var temperature = GetRandomNumber(22, 25);
        //humidity percentage
        var humidity = GetRandomNumber(75, 90);
        //utc date of the measure
        var utcDate = new Date().valueOf();

        return { type: 'meteo', message: { temperature: temperature, humidity: humidity, utcDate: utcDate } };
    }

    return {
        buildMeteoMessage: buildMeteoMessage
    };
};

exports.init = measure;